import { useState } from "react"
import axios from "axios"

const SubmitForm = () => {

    const [recipient, setRecipient] = useState('')
    const [title, setTitle] = useState('')
    const [message, setMessage] = useState('')

    const handleSubmit = async (e) => {

        const { data } = await axios.post('/api/', {
            title: title,
            message: message,
            recipient: recipient
        }, {headers: {
            'Content-Type': 'application/json'
        }})

        console.log(data)
        console.log('helol')

    };

    return (

        <form className="submit-form" onSubmit={handleSubmit}>

            <label>Recipient's Name</label>
            <input type="text" value={recipient} placeholder="Enter recipient's name" onChange={(e) => setRecipient(e.target.value)}/>

            <label>Title</label>
            <input type="text" value={title} placeholder="Write your title here" onChange={(e) => setTitle(e.target.value)}/>

            <label>Message</label>
            <input type="text" value={message} placeholder="Write your message here" onChange={(e) => setMessage(e.target.value)}/>

            <button type="button" label="hello">Submit</button>

        </form>

    )

}

export default SubmitForm;
